const todoArray = [
    {
      id : 1,
      task:"Make grocery list",
      deadline : "Thu Feb 25 2021"
    },
    {
      id : 2,
      task:"Buy a gift for a firend",
      deadline : "Fri Feb 26 2021"
    },
    {
      id : 3,
      task:"Book Dr appointment",
      deadline : "Mon Mar 01 2021"
    }
  ]

const Todoitems = ({task, deadline})=>{
    return (
        <div  style={{backgroundColor: "gray"}}>
         {task}, {deadline} 
        </div>
    )
}

const RenderTodos = ({todos})=>{

    return (
        <div>
            {
                todos.map(todo=>{
                    return<ol> <Todoitems task = {todo.task} deadline = {todo.deadline} key ={todo.id}/></ol>
                })}

        </div>
    )
}

const Todolist = ()=>{
    return (
        <div>
           <h2>Todo List</h2>
           <RenderTodos todos= {todoArray}/>
        </div>
    )
}

export default Todolist
