const Todoitems = ({task, deadline})=>{
    return (
        <div>
         <h2>   {task},{deadline}</h2>
        </div>
    )
}

const RenderTodos = ({todos})=>{

    return (
        <div>
            <h1>hi</h1>
            {
                todos.map(todo=>{
                    return <Todoitems task = {todo.task} deadline = {todo.deadline}/>
                })}

        </div>
    )
}

export default RenderTodos
