const Todoitems = ({task, deadline})=>{
    return (
        <div  style={{backgroundColor: "gray"}}>
        <h2>  {task},{deadline} </h2>
        </div>
    )
}

const RenderTodos = ({todos})=>{

    return (
        <div>
            {
                todos.map(todo=>{
                    return <Todoitems task = {todo.task} deadline = {todo.deadline} key ={todo.id}/>
                })}

        </div>
    )
}

